import React from 'react';
import Job from './Job'
import { Typography } from '@material-ui/core';
import JobModal from '../components/JobModal';
import Stepper from '../components/Stepper';
import Switch from '../components/Switch';



export default function Jobs({jobs}) {

  React.useEffect(() => {
    filterJobsbyType(jobs);
    console.log(`You clicked 5 times`);
  });

  //switches
  let filteredJobs = [];
  const [switchState, setSwitchState] = React.useState({
    partTime: false,
    fullTime: false,
    contract: true,
  });

  const typeNamesArr = ["Part Time", "Full Time", "Contract"];

  const handleChange = name => event => {
    filterJobsbyType(jobs, name);
    // console.log(name)
    // console.log(name, event.target.checked)
    setSwitchState({ ...switchState, [name]: event.target.checked });
  };

  function filterJobsbyType (jobs, name) {
    // console.log("function called", name);
    let partTimeJobs = [];
    let fullTimeJobs = [];
    let contractJobs = [];

    switch (name) {

      case 'partTime' :
        partTimeJobs = jobs.filter(job => switchState.partTime === true && job.type === typeNamesArr[0]);
        // console.log('switch case: ', name, switchState.name, jobs[0].type);

      case 'fullTime' :
        fullTimeJobs = jobs.filter(job => switchState.fullTime === true && job.type === typeNamesArr[1]);
        // console.log('switch case: ', name, switchState.name, jobs[0].type);

      case 'contract' :
        contractJobs = jobs.filter(job => switchState.contract === true && job.type === typeNamesArr[2]);
        // console.log('switch case: ', name, switchState.name, jobs[0].type);
        break;
      default:
        return jobs;
        // break;
    }
    filteredJobs = [...partTimeJobs, ...fullTimeJobs, ...contractJobs];
      console.log([...partTimeJobs, ...fullTimeJobs, ...contractJobs])
    return filteredJobs;
  }

  // console.log(jobs);

  //pagination
  const numJobs = filterJobsbyType(jobs).length;
  console.log("numJobs: ", filterJobsbyType(jobs));
  const numPages = Math.ceil(numJobs/50);
  const [activeStep, setActiveStep] = React.useState(0);
  const jobsOnPage = filterJobsbyType(jobs).slice( activeStep * 50, (activeStep * 50) + 50 );

  function handleNext() {
    setActiveStep(prevActiveStep => prevActiveStep + 1);
    window.scrollTo(0, 0)
  }

  function handleBack() {
    setActiveStep(prevActiveStep => prevActiveStep - 1);
    window.scrollTo(0, 0)
  }

  //Modal
  const [open, setOpen] = React.useState(false);
  const [selectedJob, selectJob] = React.useState({});

  function handleClickOpen() {
    setOpen(true);
    // console.log(open);
  }

  function handleClose() {
    setOpen(false);
  }


//TODO add search for terma that go to algo. seperated by commas?
  return (
    <div className='jobs'>
      <JobModal 
        open={open} 
        handleClose={handleClose} 
        job={selectedJob} 
      />
      <Typography variant="h4" component="h1">
        Entry level software jobs
      </Typography>
      <Typography variant="h6">
        Found {numJobs} Jobs
        {/* that fit {searchCriteria} */}
      </Typography>
      <Typography variant="h6" component="h1">
        Showing {activeStep * 50} - {(activeStep * 50) + 50}
      </Typography>
      <Switch 
        handleChange={handleChange} 
        switchState={switchState}
        labelsArr={typeNamesArr}
      />
      {
        jobsOnPage.map(
          ( job,i ) => <Job key={i} job={job} onClick={() => {
                          handleClickOpen()
                          selectJob(job)
                          }}
                        />
        )
      }
      <div>
        Page { activeStep + 1 } of { numPages }
      </div>
      <Stepper 
        numPages={numPages} 
        activeStep={activeStep} 
        handleNext={() => handleNext()} 
        handleBack={() => handleBack()}
      />
    </div>
  );
}

//props to stepper - steps={numPages}, activeStep={activeStep}, onClick={handleNext}, onClick={handleBack}
