import React from 'react';
import { Typography, Paper, Button } from '@material-ui/core';
import { differenceInHours, differenceInDays, differenceInMinutes } from 'date-fns';

export default function Job({ job, onClick }) {

  const today = new Date();
  const postedDate = new Date(job.created_at);

  function computeTimeDifference(current, other) {
    const minutes = Math.floor(differenceInMinutes(current, other) % 60);
    const days = differenceInDays(current, other);
    console.log(days)
    const hours = Math.floor(differenceInDays(current, other));
    const difference = `${hours} hours and ${minutes} minutes ago`;
      // if (value > 60) {
      //   console.log("total: ", value)
      //   console.log("hours: ", hours, "minutes:", minutes);
      // }
    // const difference = differenceInDays(
    //   current,
    //   other
    // )
    // make function more robust if hours > 24 output labeled days + hours
    return difference;
  }

  return (
    <Paper className='job' onClick={onClick}>
      <div>
        <Typography variant="h6">{job.company}</Typography>
        <Typography variant="h5">{job.title}</Typography>
        <Typography>{job.location}</Typography>
      </div>
      <div>
        <Typography>{`${computeTimeDifference(today, postedDate)}`}</Typography>
      </div>
    </Paper>
  );
}
