import React from 'react';
import './App.css';
import Jobs from './components/Jobs'

const JOB_API_URL = 'http://localhost:3001/jobs';

async function fetchJobs(updateCb) {
  const res = await fetch(JOB_API_URL);
  const json = await res.json();
  const sortedJson =  json;
  sortedJson.sort(function (a,b) {
    return new Date(a.created_at) - new Date(b.created_at);
  })
  const sortedAndDedupedJobs = [...new Set(sortedJson)];
  console.log("sortedJson: ",sortedJson, "length: ", sortedJson.length);
  console.log("sortedAndDedupedJobs: ", sortedAndDedupedJobs, "length: ", sortedAndDedupedJobs.length)
  updateCb(json);
}

function App() {

  const [jobsList, updateJobsList] = React.useState([]);

  React.useEffect(() => {
    fetchJobs(updateJobsList);
  },[]);

  return (
    //add suspense/lazy loading
    <div className="App">
      <Jobs jobs={jobsList} />
    </div>
  );
}

export default App;

