import React, { Component } from 'react';
import Job from './Job'
import { Typography } from '@material-ui/core';
import JobModal from '../components/JobModal';
import Stepper from '../components/Stepper';
import Switch from '../components/Switch';
import CheckboxesGroup from '../components/Checkboxes';

class Jobs extends Component {
  constructor(props) {
    super(props);
    this.state = { open: false,
                   selectedJob: {},
                   switchState: {
                    partTime: true,
                    fullTime: true,
                    contract: true,
                  },
                  activeStep: 0,
                  jobsData: this.props.jobs
    }
    this.handleChange = this.handleChange.bind(this);
    this.filterJobsbyType = this.filterJobsbyType.bind(this);
  }

  componentDidUpdate(prevProps, prevState) {
    if(prevProps.jobs !== this.props.jobs) {
      this.setState({ jobsData: this.props.jobs });
    }
    //make array of object keys
    //makr array of object values
    //.map over keys passing value as event and key as name
    // const switchStateKeys = Object.keys(this.state.switchState);
    // const switchStateValues = Object.values(this.state.switchState);
    // switchStateKeys.map((key, i) => {
    //   this.filterJobsbyType(key, switchStateValues[i]);
    // })

  }

  filterJobsbyType (name, event) {
    const { partTime, fullTime, contract} = this.state.switchState;

    let partTimeJobs = [];
    let fullTimeJobs = [];
    let contractJobs = [];
    // console.log("part time total :", this.props.jobs.filter(job => job.type === 'Part Time'));
    // console.log("full time total :", this.props.jobs.filter(job => job.type === 'Full Time'));
    // console.log("contract total :", this.props.jobs.filter(job => job.type === 'Contract'));

    if(name === "partTime" && event === true) {
      console.log("if");
      partTimeJobs = this.props.jobs.filter(job => job.type === 'Part Time');
    }

    if(name === "fullTime" && event === true) {
      console.log("if");
      fullTimeJobs = this.props.jobs.filter(job => job.type === 'Full Time');
    }

    if(name === "contract" && event === true) {
      console.log("if");
      contractJobs = this.props.jobs.filter(job => job.type === 'Contract');
    }

    this.setState({ jobsData: [...partTimeJobs, ...fullTimeJobs, ...contractJobs]});
    // console.log("test")
    // -----------------------------------------------------------------------------------------------------------------
    // works but not completely
    //checking against state for other values?
    // let output = []
    // if((name === 'partTime' && event === true) && (fullTime === false) && (contract === false)) {
    //   // this.setState({ jobsData: this.props.jobs.filter(job => job.type === "Part Time")})
    //   output = this.props.jobs.filter(job => job.type === "Part Time");
    // } 

    // if((name === 'partTime' && event === true) && (fullTime === true) && (contract === false)) {
    //   // this.setState({ jobsData: this.props.jobs.filter(job => job.type === "Part Time" || job.type === 'Full Time')})
    //   output = this.props.jobs.filter(job => job.type === "Part Time" || job.type === 'Full Time');
    //   console.log("output: ", output)
    // } 

    // if((name === 'partTime' && event === true) && (fullTime === true) && (contract === true)) {
    //   // this.setState({ jobsData: this.props.jobs.filter(job => job.type === "Part Time" || job.type === 'Full Time' || job.type === 'Contract')})
    //   output = this.props.jobs.filter(job => job.type === "Part Time" || job.type === 'Full Time' || job.type === 'Contract')
    // } 

    // if(name === 'partTime' && event === true) {
    //   this.setState({ jobsData: this.props.jobs.filter(job => job.type === "Part Time")})
    // }

    // if(name === 'partTime' && event === false) {
    //   this.setState({ jobsData: this.props.jobs.filter(job => job.type === "Full Time" || job.type === "Contract")})
    // }

    // if(name === 'fullTime' && event === true){
    //   this.setState({ jobsData: this.props.jobs.filter(job => job.type === "Full Time")})
    // }

    // if(name === 'fullTime' && event === false) {
    //   this.setState({ jobsData: this.props.jobs.filter(job => job.type === "Part Time" || job.type === "Contract")})
    // }

    // if(name === 'contract' && event === true) {
    //   this.setState({ jobsData: this.props.jobs.filter(job => job.type === "Contract")})
    // }

    // if(name === 'contract' && event === false) {
    //   this.setState({ jobsData: this.props.jobs.filter(job => job.type === "Part Time" || job.type === "Full Time")})
    // }
    // this.setState({ jobsData: output })
  }

      handleChange(name, event) {
        this.setState({ 
          switchState: {...this.state.switchState, [name]: event.target.checked}
        });
        // console.log('propsJobs: ', this.props.jobs);
        // console.log('jobsData: ', this.state.jobsData);
        this.filterJobsbyType(name, event.target.checked);
      };

      //modal open and close
      handleModalOpen() {
        this.setState({
          open: true
        })
        // setOpen(true);
      }

      handleModalClose() {
        this.setState({
          open: false
        })
        // setOpen(false);
      }

      //pagination
      handleNext() {
        this.setState((prevState) => {
          return {activeStep: prevState.activeStep + 1}
        })
        // setActiveStep(prevActiveStep => prevActiveStep + 1);
        window.scrollTo(0, 0)
      }

      handleBack() {
        this.setState((prevState) => {
          return {activeStep: prevState.activeStep - 1}
        });
        // setActiveStep(prevActiveStep => prevActiveStep - 1);
        window.scrollTo(0, 0)
      }

  render() {
    const typeNamesArr = ["Part Time", "Full Time", "Contract"];
    const {open, selectedJob, switchState, activeStep, jobsData } = this.state;
    const numJobs = jobsData.length;
    const numPages = Math.ceil(numJobs/50);
    const jobsOnPage = jobsData.slice( activeStep * 50, (activeStep * 50) + 50 );
    // console.log("switchState: ", switchState);



    return (
      <div className='jobs'>
      <JobModal 
        open={open} 
        handleClose={() => this.handleModalClose()} 
        job={selectedJob} 
      />
      <Typography variant="h4" component="h1">
        Entry level software jobs
      </Typography>
      <Typography variant="h6">
        Found {numJobs} Jobs
        {/* that fit {searchCriteria} */}
      </Typography>
      <Typography variant="h6" component="h1">
        Showing {activeStep * 50} - {(activeStep * 50) + 50}
      </Typography>
      <Switch 
        handleChange={this.handleChange} 
        switchState={switchState}
        labelsArr={typeNamesArr}
      />
      <CheckboxesGroup
        handleChange={this.handleChange} 
        switchState={switchState}
        labelsArr={typeNamesArr}
      />
      <Stepper 
        numPages={numPages} 
        activeStep={activeStep} 
        handleNext={() => this.handleNext()} 
        handleBack={() => this.handleBack()}
      />
      {
        jobsOnPage.map(
          ( job,i ) => <Job key={i} job={job} onClick={() => {
                          this.handleModalOpen()
                          // selectJob(job)
                          this.setState({
                            selectedJob: job
                          });
                        }}
                        />
        )
      }
      <div>
        Page { activeStep + 1 } of { numPages }
      </div>
      <Stepper 
        numPages={numPages} 
        activeStep={activeStep} 
        handleNext={() => this.handleNext()} 
        handleBack={() => this.handleBack()}
      />

      </div>
    );
  }
}

export default Jobs;



